// DO NOT EDIT THIS FILE
// IT WILL BE RESET TO DEAFULT BEFORE DEPLOYMENT

let cs, cs2;


function draw() {
    seedRandomness();
    clear();
    drawArt();
}

function keyPressed() {
    if (keyCode === LEFT_ARROW) {
        save();
    }
}

function windowResized() {
    clear();
    cs = min(windowHeight, windowWidth);
    cs2 = 0.5 * cs
    resizeCanvas(cs, cs);
}

function setup() {
    noLoop();
    cs = min(windowHeight, windowWidth);
    cs2 = 0.5 * cs
    createCanvas(cs, cs);
}
