// DO NOT EDIT THIS FILE
// IT WILL BE RESET TO DEAFULT BEFORE DEPLOYMENT

let pg;
let cs = 8000;
let cs2 = 4000;


function draw() {
    seedRandomness();
    pg.clear();
    drawArt();
    setImage();
}

function keyPressed() {
    if (keyCode === LEFT_ARROW) {
        save();
    }
}

function windowResized() {
    setImage();
}

function setup() {
    noLoop();
    is = min(windowHeight, windowWidth);
    createCanvas(is, is, WEBGL);
    pg = createGraphics(cs, cs);
    pg.colorMode(HSB);
    pg.pixelDensity(1);
}

function setImage() {
    clear();
    is = min(windowHeight, windowWidth);
    resizeCanvas(is, is);

    shader(myShader)
    myShader.setUniform("u_resolution", [is, is]);
    myShader.setUniform("u_background", pg);
    myShader.setUniform("u_pixeldensity", pixelDensity());
    rect(0, 0, cs, cs);
}
